INSERT INTO `tbl_city` VALUES ('15', '57', 'da-nang', 'Đà Nẵng', 'uploads/thumb/Danang.jpg', '<p>Đà Nẵng nằm giữa ba di sản thế giới. cố đô Huế, phố cổ Hội An và thánh địa Mỹ Sơn. Đà Nẵng còn có nhiều danh thắng tuyệt đẹp say lòng du khách như Ngũ Hành Sơn, Bà Nà, bán đảo Sơn Trà, đèo Hải Vân, sông Hàn thơ mộng và cầu quay Sông Hàn – niềm tự hào của thành phố, và biển Mỹ Khê đẹp nhất hành tinh.</p><p><br></p>', '0', '1');
