INSERT INTO `tbl_content_text` VALUES ('3', '3', 'Giới thiệu', '<p class=\"MsoNormal\" style=\"text-align: justify;\"><span lang=\"EN-US\" style=\"font-weight: bold;\">Bạn,</span><span lang=\"EN-US\"> sức khỏe có vấn đề nhưng đang ngần ngại đi khám vì nhiều lẽ. Liệu tình trạng này đã cần thiết phải đi khám chưa, hay mình tự ý ra tiệm thuốc? Biết đến phòng khám nào bây giờ? Liệu bác sĩ mà mình chọn đã là bác sĩ có tâm, có bắt được bệnh của mình không? Bao nhiêu câu hỏi ngổn ngang trước khám. Thế nhưng đi khám bệnh về dường như những câu hỏi còn ngổn ngang hơn. Dùng thuốc ra sao đây, mình có lỡ bỏ sót điều gì bác sĩ dặn? Tác dụng phụ trong dùng thuốc biết hỏi ai? Ôi, quên mất trong lúc khám mình không kể lần trước uống thuốc này không đỡ, giờ biết tính sao? <o:p></o:p></span></p>', '<p class=\"MsoNormal\" style=\"text-align: justify;\"><span lang=\"EN-US\" style=\"font-weight: bold;\">Tôi,</span><span lang=\"EN-US\"> người thầy thuốc hằng ngày nhận được những câu hỏi về thuốc và bệnh tật của người thân, bạn bè, làng xóm, luôn cố gắng trả lời tận tình thắc mắc của mọi người với ý thức trách nhiệm và tình cảm cao nhất. Nhưng tôi luôn khao khát một cách thức nào đó để mình có thể có ích nhiều hơn nữa. Phải có cách nào đó kéo được nhiều người bệnh lại gần hơn với những thầy thuốc có tâm. <o:p></o:p></span></p><p class=\"MsoNormal\" style=\"text-align: justify;\"><span lang=\"EN-US\" style=\"font-weight: bold;\">Chúng tôi,</span><span lang=\"EN-US\"> Antoandungthuoc.com.vn được xây dựng bởi một tập thể thầy thuốc tận tâm chính là cây cầu mà thầy thuốc và bệnh nhân đều cần. Chúng tôi cung cấp:</span><span style=\"text-indent: -18pt;\">&nbsp;</span></p><ul><li style=\"text-align: justify;\"><span lang=\"EN-US\" style=\"text-indent: -18pt;\">Giải đáp thắc mắc về thuốc và bệnh tật</span></li><li style=\"text-align: justify;\"><span lang=\"EN-US\" style=\"text-indent: -18pt;\">Hướng dẫn tìm bác sĩ và địa chỉ khám bệnh uy tín</span></li><li style=\"text-align: justify;\"><span lang=\"EN-US\" style=\"text-indent: -18pt;\">Thông tin hướng dẫn sử dụng thuốc từ các nguồn chính thống và tin cậy</span></li><li style=\"text-align: justify;\"><span lang=\"EN-US\" style=\"text-indent: -18pt;\">Hướng dẫn sử dụng cho từng đơn thuốc ở từng người bệnh cụ thể</span></li><li style=\"text-align: justify;\"><span lang=\"EN-US\" style=\"text-indent: -18pt;\">Lưu trữ hồ sơ y tế trọn đời</span></li></ul><!--[if !supportLists]--><p>&nbsp;<span style=\"text-align: justify;\">Với tâm thế làm việc vô tư, khách quan vì cộng đồng, được sự cố vấn về chuyên môn từ những bác sĩ và dược sĩ lâm sàng xuất sắc nhất, </span><span style=\"text-align: justify; font-weight: bold;\">Antoandungthuoc.com.vn</span><span style=\"text-align: justify;\"> xứng đáng với niềm tin cộng đồng trao gửi.</span></p>', '', '', '', '', '0');
INSERT INTO `tbl_content_text` VALUES ('4', '6', 'Lưu ý trước khi khám bệnh', '', '<p class=\"MsoNormal\" align=\"center\" style=\"text-align: left;\"><span lucida=\"\" sans\";\"=\"\"><span lucida=\"\" sans\";=\"\" font-size:=\"\" 16pt;\"=\"\"><span style=\"text-align: justify;\" lucida=\"\" sans\";=\"\" font-size:=\"\" 16pt;\"=\"\" sans\";\"=\"\"><span lucida=\"\" sans\";=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\"><span class=\"Apple-tab-span\" style=\"white-space: pre;\"></span>Khi bạn nghĩ rằngmình mắc bệnh hoặc có vấn đề về sức khỏe thì việc đầu tiên nên làm là đặt hẹn khám bệnh với bác sĩ.</span></span></span></span></p><p class=\"MsoNormal\" style=\"text-align:justify\"><span style=\"font-family: \" lucida=\"\" sans\";=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\"><span style=\"font-family: \" lucida=\"\" sans\";\"=\"\"><span lang=\"EN-US\" lucida=\"\" sans\";=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);=\"\" font-size:=\"\" 14pt;\"=\"\"><span lucida=\"\" sans\";=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);=\"\" font-size:=\"\" 14pt;\"=\"\"><span lucida=\"\" sans\";=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);=\"\" font-size:=\"\" 14pt;\"=\"\" sans\";\"=\"\"><span lucida=\"\" sans\";=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);=\"\" font-size:=\"\" 14pt;\"=\"\" 16pt;\"=\"\"><span class=\"Apple-tab-span\" style=\"white-space: pre;\" lucida=\"\" sans\";=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);=\"\" font-size:=\"\" 14pt;\"=\"\" 16pt;\"=\"\" sans\";\"=\"\"></span>Dù là lần khám đầutiên hay khám lại theo hẹn bạn cũng cần có sự chuẩn</span></span></span></span></span></span><span lang=\"EN-US\" lucida=\"\" sans\";=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);=\"\" font-size:=\"\" 14pt;\"=\"\"><span lucida=\"\" sans\";=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);=\"\" font-size:=\"\" 14pt;\"=\"\"><span lucida=\"\" sans\";=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);=\"\" font-size:=\"\" 14pt;\"=\"\" sans\";\"=\"\"><span lucida=\"\" sans\";=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);=\"\" font-size:=\"\" 14pt;\"=\"\" 16pt;\"=\"\">ị trước để được khám bênh,chẩn đoán và tư vấn điều trị bệnh tốt nhất từ bác sĩ. Sự chuẩn bị tưởng như đơn giản nhưng không phải người bệnh nào cũng hiểu đúng và trình bày rõ ràng vấn đề của mình khi khám bệnh</span></span></span></span><span lucida=\"\" sans\";=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);=\"\" font-size:=\"\" 14pt;\"=\"\">. Dưới đây là những lời khuyên hữu ích cho bạn khi đi khám bệnh.</span></p><p class=\"MsoNormal\" style=\"text-indent: 0px; text-align: justify;\"><span style=\"font-family: \" lucida=\"\" sans\";=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\"><span style=\"font-family: \" lucida=\"\" sans\";\"=\"\"><span lucida=\"\" sans\";=\"\" font-size:=\"\" 16pt;\"=\"\" sans\";\"=\"\" style=\"text-indent: 36pt;\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);=\"\" 14pt;\"=\"\"><span class=\"Apple-tab-span\" style=\"white-space: pre;\" lucida=\"\" sans\";=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);=\"\" font-size:=\"\" 14pt;\"=\"\"></span>Không có sự chuẩn bị quá đặc biệt nào trước mỗi lần hẹn khám bệ</span><span lucida=\"\" sans\";=\"\" font-size:=\"\" 16pt;\"=\"\" sans\";\"=\"\" style=\"text-indent: 36pt;\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);=\"\" 14pt;\"=\"\" 14pt;=\"\" letter-spacing:=\"\" 0px;\"=\"\">nh</span><span lucida=\"\" sans\";=\"\" font-size:=\"\" 16pt;\"=\"\" sans\";\"=\"\" style=\"text-indent: 36pt;\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);=\"\" 14pt;\"=\"\" 14pt;=\"\" letter-spacing:=\"\" 0px;\"=\"\">. Bạn nên mặc quần áo mỏng, dễ bộc lộ để thuận tiện cho việc thăm khám, tránh các thứcăn có thể ảnh hưởng đến kết quả thăm khám như thức ăn uống có cafein, cồn.</span></span></span></p><p class=\"MsoNormal\" style=\"text-indent: 0px; text-align: justify;\"><span style=\"font-family: \" lucida=\"\" sans\";=\"\" text-indent:=\"\" 36pt;\"=\"\"><span class=\"Apple-tab-span\" style=\"white-space:pre\"></span>Chuẩn bị nhật kí theo dõi bệnh, các kết quả khám bệnh trước đây (sổ khám bệnh, các xét nghiệm, đơn thuốc đã kê, sắp xếp theo thứ tự những đợt khámgần nhất đối với các bệnh mạn tính)</span></p><p class=\"MsoNormal\" style=\"text-indent: 0px; text-align: justify;\"><span style=\"font-family: \" lucida=\"\" sans\";=\"\" text-indent:=\"\" 36pt;\"=\"\"><span class=\"Apple-tab-span\" style=\"white-space:pre\"></span>Ghi lại những vấn đề bạn cần thảo luận với bác sĩ hay chuyên gia y tế(các xét nghiệm cần làm, các thuốc cần dùng) và mang theo checklist khi đikhám, đánh dấu và ghi lại trong suốt quá trình khám bệnh. Không quá lo lắng khihỏi về vấn đề bạn cảm thấy chưa rõ ràng, hãy để chuyên gia y tế giải thích chobạn cho tới khi bạn thực sự hiểu và cần thiết đưa người thân đi khám cùng bạn khi bạn cần sự giúp đỡ.</span></p><div style=\"font-family: \" lucida=\"\" sans\";=\"\" font-weight:=\"\" normal;=\"\" font-style:=\"\" text-decoration:=\"\" none;=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);=\"\" letter-spacing:=\"\" 0px;=\"\" font-size:=\"\" 14pt;\"=\"\"><p class=\"MsoNormal\" style=\"text-align:justify;text-indent:36.0pt\"><span lang=\"EN-US\"><span style=\"font-family: Verdana;\"><span style=\"font-family: \" lucida=\"\" sans\";\"=\"\"><span style=\"font-family: \" lucida=\"\" sans\";=\"\" font-size:=\"\" 16pt;\"=\"\"><span style=\"font-family: \" lucida=\"\" sans\";=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\"><span style=\"font-family: \" lucida=\"\" sans\";\"=\"\"><span style=\"font-style: italic;\">Một vài câu hỏi nên có trong checklist của bạn:</span></span></span></span></span></span></span></p><p class=\"MsoNormal\" style=\"text-align:justify;text-indent:36.0pt\"></p><ul><li>Các chỉ định xét nghiệm: xét nghiệm với mục đích gì, thời gian bao lâu có kết quả?</li><li>Điều trị: Có biện pháp điều trị nào khác không, tôi được khuyên nên làm gì? Có tác dụng phụ hoặc nguy cơ nào không khi điều trị? Tôi phải điều trị bao lâu, hiệu quả điều trị ra sao? Chuyện gì xảy ra nếu tôi không điều trị? Những diều tôi nên và không nên làm?...</li><li>Theo dõi: tôi có cần quay lại khám không, nếu có thì khi nào? Tôi phải liên hệ với ai khi tình trạng tệ hơn sau khi điều trị?</li></ul><p></p><p class=\"MsoNormal\" style=\"text-indent: 0px; text-align: justify;\"><span style=\"font-family: Verdana;\"><span style=\"font-family: \" lucida=\"\" sans\";\"=\"\"><span style=\"font-family: \" lucida=\"\" sans\";=\"\" font-size:=\"\" 16pt;\"=\"\"><span style=\"font-family: \" lucida=\"\" sans\";=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\"><span style=\"font-family: \" lucida=\"\" sans\";\"=\"\"><span style=\"font-weight: bold;\"><span style=\"text-indent: 36pt;\" lucida=\"\" sans\";=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);=\"\" font-size:=\"\" 14pt;\"=\"\" 16pt;\"=\"\" sans\";\"=\"\">Mách nhỏ cho bạn:</span></span></span></span></span></span></span></p><p class=\"MsoNormal\" style=\"text-align:justify\"><span style=\"font-style: italic;\"><span lang=\"EN-US\" style=\"text-decoration: underline;\"><span style=\"font-family: Verdana;\"><span style=\"font-family: \" lucida=\"\" sans\";\"=\"\"><span style=\"font-family: \" lucida=\"\" sans\";=\"\" font-size:=\"\" 16pt;\"=\"\"><span style=\"font-family: \" lucida=\"\" sans\";=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\"><span style=\"font-family: \" lucida=\"\" sans\";\"=\"\">Trước khi khám bệnh:</span></span></span></span></span></span></span></p><p class=\"MsoNormal\" style=\"text-align:justify\"><span lang=\"EN-US\"><span style=\"font-family: Verdana;\"><span lucida=\"\" sans\";\"=\"\"><span lucida=\"\" sans\";=\"\" font-size:=\"\" 16pt;\"=\"\"><span lucida=\"\" sans\";=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\"><span lucida=\"\" sans\";\"=\"\">Liên hệ với Antoandungthuoc.com.vn để được:</span></span></span></span></span></span></p><p class=\"MsoNormal\" style=\"text-align:justify\"></p><ul><li><span style=\"font-family: \" lucida=\"\" sans\";\"=\"\">Hướng dẫn những vấn đề cần hỏi về bệnh của bạn</span></li><li><span style=\"font-family: \" lucida=\"\" sans\";\"=\"\">Định hướng khám đúng chuyên khoa, chọn đúng thầy thuốc và địa chỉ khám bệnh phù hợp</span></li><li><span style=\"font-family: \" lucida=\"\" sans\";\"=\"\">Cung cấp lịch sử khám chữa bệnh của bạn</span></li></ul><p></p><p class=\"MsoNormal\" style=\"text-align:justify\"><span style=\"text-decoration: underline; font-style: italic;\"><span style=\"font-family: Verdana;\"><span style=\"font-family: \" lucida=\"\" sans\";\"=\"\"><span style=\"font-family: \" lucida=\"\" sans\";=\"\" font-size:=\"\" 16pt;\"=\"\"><span style=\"font-family: \" lucida=\"\" sans\";=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\"><span style=\"font-family: \" lucida=\"\" sans\";\"=\"\">Trong khi khám bệnh:</span></span></span></span></span></span></p><p class=\"MsoNormal\" style=\"text-align:justify\"></p><ul><li><span style=\"font-family: \" lucida=\"\" sans\";\"=\"\">Đừng e ngại việc hỏi lại khi bạnkhông hiểu vấn đề, đặt câu hỏi: <span lucida=\"\" sans\";=\"\" font-size:=\"\" 16pt;\"=\"\" sans\";\"=\"\" 14pt;=\"\" text-indent:=\"\" -18pt;\"=\"\">“ Có thểnhắc lại lần nữa không?” </span><span lucida=\"\" sans\";=\"\" font-size:=\"\" 14pt;=\"\" text-indent:=\"\" -18pt;\"=\"\">hoặc</span><span lucida=\"\" sans\";=\"\" font-size:=\"\" 16pt;\"=\"\" sans\";\"=\"\" 14pt;=\"\" text-indent:=\"\" -18pt;\"=\"\"> “ Xin lỗi,tôi vẫn chưa hiểu”</span></span></li><li><span style=\"font-family: \" lucida=\"\" sans\";\"=\"\">Nếu chuyên gia y tế giải thíchvà bạn không hiểu , hãy yêu cầu họ viết ra và giải thích cho bạn hoặc nhờ sựgiúp đỡ của người thân đi cùng bạn. Bởi thời gian khám và tư vấn cho mỗi người bệnh không nhiều nên bạn cần hiểu và trình bày rõ ràng vấn đề của mình với nhânviên y tế.</span></li><li><span style=\"font-family: \" lucida=\"\" sans\";\"=\"\">Viết lại những điều bạn thấy cầnthiết hoặc nhờ người thân viết lại.</span></li></ul><p></p><p class=\"MsoNormal\" style=\"text-align:justify\"><span style=\"font-style: italic; text-decoration: underline; font-family: Verdana;\">Trước khi kết thúc khám bệnh</span></p><ul><li><span lucida=\"\" sans\";\"=\"\">Kiểm tra: checklist của bạn đã hoàn thành hết và bạn nắm được tình hình bệnh tật của mình. Bạn đã biết và ghi lại tiến trình điều trị và theo dõi.</span></li></ul><p class=\"MsoNormal\" style=\"text-align:justify\"><span style=\"font-style: italic; text-decoration: underline; font-family: Verdana;\">Trước khi dùng thuốc</span></p><p class=\"MsoNormal\" style=\"text-align:justify\"><span style=\"font-family: Verdana;\">Liên hệ với Antoandungthuoc.com.vn để được</span></p><ul><li>Hướng dẫn dùng thuốc và kiểm tra tương tác thuốc</li><li>Giải đáp bất kỳ thắc mắc và hướng dẫn xử trí những vấn đề phát sinh trong quá trình dùng thuốc</li><li><span style=\"font-family: \" lucida=\"\" sans\";\"=\"\">Lưu lịch sử khám chữa bệnh của bạn.</span></li></ul><p></p></div><p class=\"MsoNormal\" style=\"text-align: justify;\"><span lang=\"EN-US\" style=\"font-style: italic;\"><span style=\"font-weight: bold;\"><span style=\"font-family: Verdana;\"><span style=\"font-family: \" lucida=\"\" sans\";\"=\"\"><span style=\"font-family: \" lucida=\"\" sans\";=\"\" font-size:=\"\" 16pt;\"=\"\"><span style=\"font-family: \" lucida=\"\" sans\";=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\"><span style=\"font-family: \" lucida=\"\" sans\";\"=\"\">Hãy có trách nhiệm với sức khỏe củamình và là một người bệnh sáng suốt!</span></span></span></span></span></span><span style=\"color: rgb(255, 0, 0);\"><o:p></o:p></span></span></p>', 'dsd', 'dsd', 'sdsds', '', '0');
INSERT INTO `tbl_content_text` VALUES ('5', '7', 'Sai sót trong sử dụng thuốc', '', '<p class=\"MsoNormal\" align=\"center\" style=\"text-align: left;\"><span style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">Hầu hết mọi người trong thế giới hiện đại đều sử\r\ndụng thuốc ít nhất một lần hoặc nhiều hơn. Theo một ước tính, nếu được phép\r\ntrong 1 tuần thì cứ 4 trong 5 người sử dụng thuốc kê đơn, thuốc OTC, thực phẩm\r\nchức năng và có gần một phần ba người trưởng thành sử dụng 5 hoặc nhiều hơn các\r\nthuốc khác nhau.</span></span></span></span></p><p class=\"MsoNormal\" style=\"text-align:justify;tab-stops:205.55pt\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">Thuốc để\r\nđiều trị bệnh nhiễm khuẩn, đề phòng biến chứng của các bệnh mạn tính và thuốc\r\ngiảm đau được hàng triệu người sử dụng mỗi ngày nhưng thuốc có thể gây hại. Khi\r\nmột người bị ảnh hưởng không có lợi bởi việc dùng thuốc, họ được coi là có biến\r\ncố bất lợi liên quan đến thuốc (Adverse drug events – ADEs) gọi chung cho những\r\nvấn đề không mong muốn xảy đến trong quá trình dùng thuốc. Những biến cố này có\r\nthể do tác dụng phụ của thuốc hoặc do cách dùng thuốc (giảm liều, quá liều, ngừng\r\nđiều trị….) <span style=\"font-family: Arial;\">(1). </span><o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">Theo Trung tâm kiểm soát và phòng ngừa dịch bệnh\r\nHoa Kỳ (CDC), biến cố bất lợi khi dùng thuốc (ADEs) đang là vấn đề y tế dự\r\nphòng nghiêm trọng. Ước tính rằng:<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoListParagraphCxSpFirst\" style=\"text-align: justify; margin-left: 54pt; text-indent: -18pt;\"><!--[if !supportLists]--><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">-<span style=\"font-stretch: normal; line-height: normal; font-family: Arial;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><!--[endif]-->82% người Mỹ sử dụng thuốc ít nhất một lần\r\ntrong đời và 29% uống 5 loại thuôc hoặc nhiều hơn.<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoListParagraphCxSpMiddle\" style=\"text-align: justify; margin-left: 54pt; text-indent: -18pt;\"><!--[if !supportLists]--><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">-<span style=\"font-stretch: normal; line-height: normal; font-family: Arial;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><!--[endif]-->700,000 lượt vào khoa cấp cứu và 120,000 trường\r\nhợp phải nhập viện do ADEs mỗi năm.<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoListParagraphCxSpMiddle\" style=\"text-align: justify; margin-left: 54pt; text-indent: -18pt;\"><!--[if !supportLists]--><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">-<span style=\"font-stretch: normal; line-height: normal; font-family: Arial;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><!--[endif]-->3.5 tỉ đô la Mỹ là chi phí y tế hàng năm cho\r\nADEs.<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoListParagraphCxSpLast\" style=\"text-align: justify; margin-left: 54pt; text-indent: -18pt;\"><!--[if !supportLists]--><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">-<span style=\"font-stretch: normal; line-height: normal; font-family: Arial;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</span><!--[endif]-->Ít nhất\r\n40% các trường hợp xảy ra ADEs mà không phải nhập viện có thể phòng tránh được.<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">Biến cố bất lợi khi dùng thuốc xuất hiện ngày\r\ncàng nhiều bởi:&nbsp; <o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoListParagraphCxSpFirst\" style=\"text-align: justify; margin-left: 54pt; text-indent: -18pt;\"><!--[if !supportLists]--><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">-<span style=\"font-stretch: normal; line-height: normal; font-family: Arial;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><!--[endif]-->Sự phát triển của các thuốc mới<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoListParagraphCxSpMiddle\" style=\"text-align: justify; margin-left: 54pt; text-indent: -18pt;\"><!--[if !supportLists]--><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">-<span style=\"font-stretch: normal; line-height: normal; font-family: Arial;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><!--[endif]-->Chỉ định mới của thuốc<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoListParagraphCxSpMiddle\" style=\"text-align: justify; margin-left: 54pt; text-indent: -18pt;\"><!--[if !supportLists]--><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">-<span style=\"font-stretch: normal; line-height: normal; font-family: Arial;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><!--[endif]-->Việc tăng sử dụng thuốc để phòng bệnh<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoListParagraphCxSpLast\" style=\"text-align: justify; margin-left: 54pt; text-indent: -18pt;\"><!--[if !supportLists]--><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">-<span style=\"font-stretch: normal; line-height: normal; font-family: Arial;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</span><!--[endif]-->Tăng số lượng\r\nthuốc trong toa kê <span style=\"font-family: Arial;\">(2)</span><o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">Tất cả những biến cố liên quan đến dùng thuốc đều\r\ncó thể phòng tránh được.<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">Phòng ngừa các biến cố bất lợi liên quan đến\r\nthuốc </span><span style=\"font-family: Arial;\">(3)</span><span style=\"font-family: Arial;\"><o:p></o:p></span></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-style: italic;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">Bạn\r\nnên:<o:p></o:p></span></span></span></span></p>\r\n\r\n<p class=\"MsoListParagraphCxSpFirst\" style=\"text-align: justify; margin-left: 54pt; text-indent: -18pt;\"><!--[if !supportLists]--><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">-<span style=\"font-stretch: normal; line-height: normal; font-family: Arial;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><!--[endif]-->Có danh sách các thuốc cần dùng<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoListParagraphCxSpMiddle\" style=\"text-align: justify; margin-left: 54pt; text-indent: -18pt;\"><!--[if !supportLists]--><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">-<span style=\"font-stretch: normal; line-height: normal; font-family: Arial;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><!--[endif]-->Sử dụng thuốc theo hướng dẫn<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoListParagraphCxSpMiddle\" style=\"text-align: justify; margin-left: 54pt; text-indent: -18pt;\"><!--[if !supportLists]--><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">-<span style=\"font-stretch: normal; line-height: normal; font-family: Arial;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><!--[endif]-->Đặt câu hỏi, thắc mắc liên quan đến thuốc với\r\nnhân viên y tế<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoListParagraphCxSpLast\" style=\"text-align: justify; margin-left: 54pt; text-indent: -18pt;\"><!--[if !supportLists]--><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">-<span style=\"font-stretch: normal; line-height: normal; font-family: Arial;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</span><!--[endif]-->Theo dõi\r\nthường xuyên bất kì xét nghiệm máu được bác sĩ yêu cầu<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">Một số bệnh nhất thiết cần xét nghiệm máu để\r\nchẩn đoán bệnh và chỉ định liều thuốc chính xác. Gần 40% các trường hợp cấp cứu\r\nphải nhập viện điều trị do một số thuốc mà cần kiểm soát tác dụng bằng xét nghiệm\r\nmáu<o:p></o:p></span></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial; font-style: italic;\">Tips: <o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">Hãy hỏi chuyên gia y tế nếu bạn cần làm xét\r\nnghiệm máu. Hỏi bác sỹ hoặc dược sỹ nếu bạn dang uống thuốc mà cần kiểm soát bằng\r\nxét nghiệm máu và cần chú ý đặc biệt gì khi dùng thuốc và theo dõi, kiểm tra\r\nxét nghiệm máu thường xuyên. <o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">Một số thuốc cần được kiểm soát: thuốc chống\r\nđông (warfarin…), thuốc điều trị đái tháo đường (Insulin..), thuốc chống co giật\r\n(phenytion, carbamazepin…), thuốc tim mạch (Digoxin…)…<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style=\"font-family: Arial;\">Việc vô tình sử dụng thuốc giảm đau quá liều\r\ncó thể nguy hiểm đến tính mạng<o:p></o:p></span></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">Sử dụng thuốc giảm đau giúp cải thiện chất lượng\r\ncuộc sống tuy nhiên hàng năm có hàng triệu người chết do vô tình sử dụng quá liều\r\nthuốc giảm đau Opioids (methadone, oxycodone và hydrocodone), con số này còn\r\nnhiều hơn số người chết vì cocaine hay heroin.<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">&nbsp;Tips:&nbsp; <o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">Sử dụng thuốc giảm đau theo hướng dẫn. Nếu bạn\r\nđang uống thuốc giảm đau Opioids (thuốc có codein, ultracet…) hãy chắc chắn rằng\r\nbác sĩ của bạn đã biết tất cả các thuốc bạn đang dùng vì một số thuốc khi uống\r\ncùng thuốc giảm đau có thể gây quá liều, nguy hiểm tính mạng<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">&nbsp;<span style=\"font-family: Arial;\">Một số thuốc không có hiệu quả tốt với sức\r\nkhỏe của bạn<o:p></o:p></span></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">Kháng sinh có tác dụng diệt vi khuẩn chứ không\r\ndiệt virus. Hầu hết cảm lạnh, ho , cúm, đau họng và chảy nước mũi do virus gây\r\nnên. Sử dụng kháng sinh cho những nhiễm khuẩn do virus không chữa khỏi bệnh hoặc\r\nlàm bạn khỏe hơn. Mặc dù kháng sinh là thuốc điều trị cho nhiễm khuẩn nhưng vẫn\r\ncó những thuốc gây nên những biến cố bất lợi nguy hiểm mà cần phải đến bệnh viện\r\ncấp cứu. Gần 100.000 người lớn phải vào khoa cấp cứu mỗi năm bởi những biến cố\r\nbất lợi liên quan đến kháng sinh.<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">&nbsp;Tips:<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">Không yêu kháng sinh khi bác sỹ khẳng định dùng\r\nkháng sinh là không cần thiết. Kháng sinh không trị khỏi nhiễm khuẩn do virus.<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">Nếu bạn được chỉ định dùng kháng sinh, hãy chắc\r\nchắn uống hết thuốc được kê kể cả khi thấy khỏi bệnh. Không để dành kháng sinh\r\ncho “lần sau”. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></span></span></p>\r\n\r\n<p class=\"MsoListParagraphCxSpFirst\" style=\"text-align: justify; text-indent: -18pt;\"><!--[if !supportLists]--><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span class=\"MsoHyperlink\" style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">(1)<span style=\"font-stretch: normal; line-height: normal; font-family: Arial;\">&nbsp;\r\n</span></span></span><!--[endif]--><span lang=\"VI\" style=\"font-family: Arial;\"><a href=\"http://canhgiacduoc.org.vn/CanhGiacDuoc/phanungcohai.aspx\"><span lang=\"EN-US\" style=\"font-family: Arial;\">http://canhgiacduoc.org.vn/CanhGiacDuoc/phanungcohai.aspx</span></a></span><span class=\"MsoHyperlink\" style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><o:p></o:p></span></span></span></span></span></p>\r\n\r\n<p class=\"MsoListParagraphCxSpMiddle\" style=\"text-align: justify; text-indent: -18pt;\"><!--[if !supportLists]--><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">(2)<span style=\"font-stretch: normal; line-height: normal; font-family: Arial;\">&nbsp;\r\n</span><!--[endif]--><span lang=\"VI\" style=\"font-family: Arial;\"><a href=\"http://www.cdc.gov/medicationsafety/basics.html#factsheets\"><span lang=\"EN-US\" style=\"font-family: Arial;\">http://www.cdc.gov/medicationsafety/basics.html#factsheets</span></a></span><o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoListParagraphCxSpLast\" style=\"text-align: justify; text-indent: -18pt;\"><!--[if !supportLists]--><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">(3)<span style=\"font-stretch: normal; line-height: normal; font-family: Arial;\">&nbsp;\r\n</span><!--[endif]--><span lang=\"VI\" style=\"font-family: Arial;\"><span lang=\"EN-US\" style=\"font-family: Arial;\"><a href=\"http://www.cdc.gov/medicationsafety/adult_adversedrugevents.html\">http://www.cdc.gov/medicationsafety/adult_adversedrugevents.html</a></span></span></span></span></span></p>', 'sai sót trong dùng thuốc', '', '', '', '0');
INSERT INTO `tbl_content_text` VALUES ('6', '8', 'Sai sót trong sử dụng thuốc', '', '<p class=\"MsoNormal\" align=\"center\" style=\"text-align: left;\"><span style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">Hầu hết mọi người trong thế giới hiện đại đều sử\r\ndụng thuốc ít nhất một lần hoặc nhiều hơn. Theo một ước tính, nếu được phép\r\ntrong 1 tuần thì cứ 4 trong 5 người sử dụng thuốc kê đơn, thuốc OTC, thực phẩm\r\nchức năng và có gần một phần ba người trưởng thành sử dụng 5 hoặc nhiều hơn các\r\nthuốc khác nhau.</span></span></span></span></p><p class=\"MsoNormal\" style=\"text-align:justify;tab-stops:205.55pt\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">Thuốc để\r\nđiều trị bệnh nhiễm khuẩn, đề phòng biến chứng của các bệnh mạn tính và thuốc\r\ngiảm đau được hàng triệu người sử dụng mỗi ngày nhưng thuốc có thể gây hại. Khi\r\nmột người bị ảnh hưởng không có lợi bởi việc dùng thuốc, họ được coi là có biến\r\ncố bất lợi liên quan đến thuốc (Adverse drug events – ADEs) gọi chung cho những\r\nvấn đề không mong muốn xảy đến trong quá trình dùng thuốc. Những biến cố này có\r\nthể do tác dụng phụ của thuốc hoặc do cách dùng thuốc (giảm liều, quá liều, ngừng\r\nđiều trị….) <span style=\"font-family: Arial;\">(1). </span><o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">Theo Trung tâm kiểm soát và phòng ngừa dịch bệnh\r\nHoa Kỳ (CDC), biến cố bất lợi khi dùng thuốc (ADEs) đang là vấn đề y tế dự\r\nphòng nghiêm trọng. Ước tính rằng:<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoListParagraphCxSpFirst\" style=\"text-align: justify; margin-left: 54pt; text-indent: -18pt;\"><!--[if !supportLists]--><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">-<span style=\"font-stretch: normal; line-height: normal; font-family: Arial;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><!--[endif]-->82% người Mỹ sử dụng thuốc ít nhất một lần\r\ntrong đời và 29% uống 5 loại thuôc hoặc nhiều hơn.<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoListParagraphCxSpMiddle\" style=\"text-align: justify; margin-left: 54pt; text-indent: -18pt;\"><!--[if !supportLists]--><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">-<span style=\"font-stretch: normal; line-height: normal; font-family: Arial;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><!--[endif]-->700,000 lượt vào khoa cấp cứu và 120,000 trường\r\nhợp phải nhập viện do ADEs mỗi năm.<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoListParagraphCxSpMiddle\" style=\"text-align: justify; margin-left: 54pt; text-indent: -18pt;\"><!--[if !supportLists]--><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">-<span style=\"font-stretch: normal; line-height: normal; font-family: Arial;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><!--[endif]-->3.5 tỉ đô la Mỹ là chi phí y tế hàng năm cho\r\nADEs.<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoListParagraphCxSpLast\" style=\"text-align: justify; margin-left: 54pt; text-indent: -18pt;\"><!--[if !supportLists]--><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">-<span style=\"font-stretch: normal; line-height: normal; font-family: Arial;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</span><!--[endif]-->Ít nhất\r\n40% các trường hợp xảy ra ADEs mà không phải nhập viện có thể phòng tránh được.<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">Biến cố bất lợi khi dùng thuốc xuất hiện ngày\r\ncàng nhiều bởi:&nbsp; <o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoListParagraphCxSpFirst\" style=\"text-align: justify; margin-left: 54pt; text-indent: -18pt;\"><!--[if !supportLists]--><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">-<span style=\"font-stretch: normal; line-height: normal; font-family: Arial;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><!--[endif]-->Sự phát triển của các thuốc mới<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoListParagraphCxSpMiddle\" style=\"text-align: justify; margin-left: 54pt; text-indent: -18pt;\"><!--[if !supportLists]--><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">-<span style=\"font-stretch: normal; line-height: normal; font-family: Arial;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><!--[endif]-->Chỉ định mới của thuốc<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoListParagraphCxSpMiddle\" style=\"text-align: justify; margin-left: 54pt; text-indent: -18pt;\"><!--[if !supportLists]--><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">-<span style=\"font-stretch: normal; line-height: normal; font-family: Arial;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><!--[endif]-->Việc tăng sử dụng thuốc để phòng bệnh<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoListParagraphCxSpLast\" style=\"text-align: justify; margin-left: 54pt; text-indent: -18pt;\"><!--[if !supportLists]--><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">-<span style=\"font-stretch: normal; line-height: normal; font-family: Arial;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</span><!--[endif]-->Tăng số lượng\r\nthuốc trong toa kê <span style=\"font-family: Arial;\">(2)</span><o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">Tất cả những biến cố liên quan đến dùng thuốc đều\r\ncó thể phòng tránh được.<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">Phòng ngừa các biến cố bất lợi liên quan đến\r\nthuốc </span><span style=\"font-family: Arial;\">(3)</span><span style=\"font-family: Arial;\"><o:p></o:p></span></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-style: italic;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">Bạn\r\nnên:<o:p></o:p></span></span></span></span></p>\r\n\r\n<p class=\"MsoListParagraphCxSpFirst\" style=\"text-align: justify; margin-left: 54pt; text-indent: -18pt;\"><!--[if !supportLists]--><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">-<span style=\"font-stretch: normal; line-height: normal; font-family: Arial;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><!--[endif]-->Có danh sách các thuốc cần dùng<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoListParagraphCxSpMiddle\" style=\"text-align: justify; margin-left: 54pt; text-indent: -18pt;\"><!--[if !supportLists]--><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">-<span style=\"font-stretch: normal; line-height: normal; font-family: Arial;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><!--[endif]-->Sử dụng thuốc theo hướng dẫn<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoListParagraphCxSpMiddle\" style=\"text-align: justify; margin-left: 54pt; text-indent: -18pt;\"><!--[if !supportLists]--><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">-<span style=\"font-stretch: normal; line-height: normal; font-family: Arial;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><!--[endif]-->Đặt câu hỏi, thắc mắc liên quan đến thuốc với\r\nnhân viên y tế<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoListParagraphCxSpLast\" style=\"text-align: justify; margin-left: 54pt; text-indent: -18pt;\"><!--[if !supportLists]--><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">-<span style=\"font-stretch: normal; line-height: normal; font-family: Arial;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</span><!--[endif]-->Theo dõi\r\nthường xuyên bất kì xét nghiệm máu được bác sĩ yêu cầu<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style=\"font-family: Arial;\">Một số bệnh nhất thiết cần xét nghiệm máu để\r\nchẩn đoán bệnh và chỉ định liều thuốc chính xác. Gần 40% các trường hợp cấp cứu\r\nphải nhập viện điều trị do một số thuốc mà cần kiểm soát tác dụng bằng xét nghiệm\r\nmáu<o:p></o:p></span></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">&nbsp;Tips: <o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">Hãy hỏi chuyên gia y tế nếu bạn cần làm xét\r\nnghiệm máu. Hỏi bác sỹ hoặc dược sỹ nếu bạn dang uống thuốc mà cần kiểm soát bằng\r\nxét nghiệm máu và cần chú ý đặc biệt gì khi dùng thuốc và theo dõi, kiểm tra\r\nxét nghiệm máu thường xuyên. <o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">Một số thuốc cần được kiểm soát: thuốc chống\r\nđông (warfarin…), thuốc điều trị đái tháo đường (Insulin..), thuốc chống co giật\r\n(phenytion, carbamazepin…), thuốc tim mạch (Digoxin…)…<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style=\"font-family: Arial;\">Việc vô tình sử dụng thuốc giảm đau quá liều\r\ncó thể nguy hiểm đến tính mạng<o:p></o:p></span></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">Sử dụng thuốc giảm đau giúp cải thiện chất lượng\r\ncuộc sống tuy nhiên hàng năm có hàng triệu người chết do vô tình sử dụng quá liều\r\nthuốc giảm đau Opioids (methadone, oxycodone và hydrocodone), con số này còn\r\nnhiều hơn số người chết vì cocaine hay heroin.<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">&nbsp;Tips:&nbsp; <o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">Sử dụng thuốc giảm đau theo hướng dẫn. Nếu bạn\r\nđang uống thuốc giảm đau Opioids (thuốc có codein, ultracet…) hãy chắc chắn rằng\r\nbác sĩ của bạn đã biết tất cả các thuốc bạn đang dùng vì một số thuốc khi uống\r\ncùng thuốc giảm đau có thể gây quá liều, nguy hiểm tính mạng<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style=\"font-family: Arial;\">Một số thuốc không có hiệu quả tốt với sức\r\nkhỏe của bạn<o:p></o:p></span></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">Kháng sinh có tác dụng diệt vi khuẩn chứ không\r\ndiệt virus. Hầu hết cảm lạnh, ho , cúm, đau họng và chảy nước mũi do virus gây\r\nnên. Sử dụng kháng sinh cho những nhiễm khuẩn do virus không chữa khỏi bệnh hoặc\r\nlàm bạn khỏe hơn. Mặc dù kháng sinh là thuốc điều trị cho nhiễm khuẩn nhưng vẫn\r\ncó những thuốc gây nên những biến cố bất lợi nguy hiểm mà cần phải đến bệnh viện\r\ncấp cứu. Gần 100.000 người lớn phải vào khoa cấp cứu mỗi năm bởi những biến cố\r\nbất lợi liên quan đến kháng sinh.<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">&nbsp;Tips:<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">Không yêu kháng sinh khi bác sỹ khẳng định dùng\r\nkháng sinh là không cần thiết. Kháng sinh không trị khỏi nhiễm khuẩn do virus.<o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">Nếu bạn được chỉ định dùng kháng sinh, hãy chắc\r\nchắn uống hết thuốc được kê kể cả khi thấy khỏi bệnh. Không để dành kháng sinh\r\ncho “lần sau”. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></span></span></p>\r\n\r\n<p class=\"MsoListParagraphCxSpFirst\" style=\"text-align: justify; text-indent: -18pt;\"><!--[if !supportLists]--><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span class=\"MsoHyperlink\" style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">(1)<span style=\"font-stretch: normal; line-height: normal; font-family: Arial;\">&nbsp;\r\n</span></span></span><!--[endif]--><span lang=\"VI\" style=\"font-family: Arial;\"><a href=\"http://canhgiacduoc.org.vn/CanhGiacDuoc/phanungcohai.aspx\"><span lang=\"EN-US\" style=\"font-family: Arial;\">http://canhgiacduoc.org.vn/CanhGiacDuoc/phanungcohai.aspx</span></a></span><span class=\"MsoHyperlink\" style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><o:p></o:p></span></span></span></span></span></p>\r\n\r\n<p class=\"MsoListParagraphCxSpMiddle\" style=\"text-align: justify; text-indent: -18pt;\"><!--[if !supportLists]--><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">(2)<span style=\"font-stretch: normal; line-height: normal; font-family: Arial;\">&nbsp;\r\n</span><!--[endif]--><span lang=\"VI\" style=\"font-family: Arial;\"><a href=\"http://www.cdc.gov/medicationsafety/basics.html#factsheets\"><span lang=\"EN-US\" style=\"font-family: Arial;\">http://www.cdc.gov/medicationsafety/basics.html#factsheets</span></a></span><o:p></o:p></span></span></span></p>\r\n\r\n<p class=\"MsoListParagraphCxSpLast\" style=\"text-align: justify; text-indent: -18pt;\"><!--[if !supportLists]--><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\"><span style=\"font-family: Arial;\">(3)<span style=\"font-stretch: normal; line-height: normal; font-family: Arial;\">&nbsp;\r\n</span><!--[endif]--><span lang=\"VI\" style=\"font-family: Arial;\"><span lang=\"EN-US\" style=\"font-family: Arial;\"><a href=\"http://www.cdc.gov/medicationsafety/adult_adversedrugevents.html\">http://www.cdc.gov/medicationsafety/adult_adversedrugevents.html</a></span></span></span></span></span></p>', '', '', '', '', '0');
