INSERT INTO `tbl_drug_text` VALUES ('3', '3', 'Fenofibrate', '', 'fenofibrate', null, null, '<p class=\"MsoNormal\" style=\"text-align: justify;\"><span lang=\"EN-US\" style=\"font-weight: bold;\">Bạn,</span><span lang=\"EN-US\"> sức khỏe có vấn đề nhưng đang ngần ngại đi khám vì nhiều lẽ. Liệu tình trạng này đã cần thiết phải đi khám chưa, hay mình tự ý ra tiệm thuốc? Biết đến phòng khám nào bây giờ? Liệu bác sĩ mà mình chọn đã là bác sĩ có tâm, có bắt được bệnh của mình không? Bao nhiêu câu hỏi ngổn ngang trước khám. Thế nhưng đi khám bệnh về dường như những câu hỏi còn ngổn ngang hơn. Dùng thuốc ra sao đây, mình có lỡ bỏ sót điều gì bác sĩ dặn? Tác dụng phụ trong dùng thuốc biết hỏi ai? Ôi, quên mất trong lúc khám mình không kể lần trước uống thuốc này không đỡ, giờ biết tính sao? <o:p></o:p></span></p>', '[\"<span style=\\\"font-weight: bold;\\\">Fenofibrate l\\u00e0 thu\\u1ed1c g\\u00ec?<\\/span><div><span style=\\\"font-weight: bold;\\\"><br><\\/span><div>Fenofibrate l\\u00e0 thu\\u1ed1c l\\u00e0m gi\\u1ea3m Lipid trong huy\\u1ebft thanh, l\\u00e0m gi\\u1ea3m cholesterol v\\u00e0 triglicerid. M\\u00e3 ATC: C10 AB 05; \\u0110\\u01b0\\u1ee3c s\\u1eed d\\u1ee5ng trong \\u0111i\\u1ec1u tr\\u1ecb r\\u1ed1i lo\\u1ea1n lipoprotein huy\\u1ebft c\\u00e1c typ IIa, IIb, III, IV v\\u00e0 V, ph\\u1ed1i h\\u1ee3p v\\u1edbi ch\\u1ebf \\u0111\\u1ed9 \\u0103n.<\\/div><div><br><\\/div><div><span style=\\\"font-weight: bold;\\\">Khi n\\u00e0o kh\\u00f4ng n\\u00ean d\\u00f9ng thu\\u1ed1c n\\u00e0y?<\\/span><\\/div><div><br><\\/div><div>Fenofibrate ch\\u1ed1ng ch\\u1ec9 \\u0111\\u1ecbnh trong c\\u00e1c tr\\u01b0\\u1eddng h\\u1ee3p b\\u1ec7nh nh\\u00e2n m\\u1eabn c\\u1ea3m v\\u1edbi Fibrat ho\\u0103c t\\u00e1 d\\u01b0\\u1ee3c d\\u00f9ng l\\u00e0m thu\\u1ed1c, suy th\\u1eadn n\\u1eb7ng, r\\u1ed1i lo\\u1ea1n ch\\u1ee9c n\\u0103ng gan n\\u1eb7ng, tr\\u1ebb d\\u01b0\\u1edbi 10 tu\\u1ed5i.<\\/div><div><br><\\/div><div><span style=\\\"font-weight: bold;\\\">Nh\\u1eefng \\u0111i\\u1ec1u c\\u1ea7n th\\u1eadn tr\\u1ecdng khi d\\u00f9ng thu\\u1ed1c n\\u00e0y<\\/span><\\/div><div><br><\\/div><div>Nh\\u1ea5t thi\\u1ebft ph\\u1ea3i th\\u0103m d\\u00f2 ch\\u1ee9c n\\u0103ng gan v\\u00e0 th\\u1eadn c\\u1ee7a ng\\u01b0\\u1eddi b\\u1ec7nh tr\\u01b0\\u1edbc khi b\\u1eaft \\u0111\\u1ea7u d\\u00f9ng fibrat.<\\/div><div><br><\\/div><div>\\u1ede ng\\u01b0\\u1eddi b\\u1ec7nh u\\u1ed1ng thu\\u1ed1c ch\\u1ed1ng \\u0111\\u00f4ng m\\u00e1u: Khi b\\u1eaft \\u0111\\u1ea7u d\\u00f9ng fibrat, c\\u1ea7n gi\\u1ea3m li\\u1ec1u thu\\u1ed1c ch\\u1ed1ng \\u0111\\u00f4ng xu\\u1ed1ng ch\\u1ec9 c\\u00f2n m\\u1ed9t ph\\u1ea7n ba li\\u1ec1u c\\u0169 v\\u00e0 \\u0111i\\u1ec1u ch\\u1ec9nh n\\u1ebfu c\\u1ea7n. C\\u1ea7n theo d\\u00f5i th\\u01b0\\u1eddng xuy\\u00ean h\\u01a1n l\\u01b0\\u1ee3ng prothrombin m\\u00e1u. \\u0110i\\u1ec1u ch\\u1ec9nh li\\u1ec1u thu\\u1ed1c ch\\u1ed1ng \\u0111\\u00f4ng trong qu\\u00e1 tr\\u00ecnh d\\u00f9ng v\\u00e0 sau khi ng\\u1eebng d\\u00f9ng fibrat 8 ng\\u00e0y.<\\/div><div><br><\\/div><div>Nh\\u01b0\\u1ee3c n\\u0103ng gi\\u00e1p c\\u00f3 th\\u1ec3 l\\u00e0 m\\u1ed9t y\\u1ebfu t\\u1ed1 l\\u00e0m t\\u0103ng kh\\u1ea3 n\\u0103ng b\\u1ecb t\\u00e1c d\\u1ee5ng ph\\u1ee5 \\u1edf c\\u01a1.<\\/div><div><br><\\/div><div>C\\u1ea7n \\u0111o transaminase 3 th\\u00e1ng m\\u1ed9t l\\u1ea7n, trong 12 th\\u00e1ng \\u0111\\u1ea7u d\\u00f9ng thu\\u1ed1c. Ph\\u1ea3i t\\u1ea1m ng\\u1eebng d\\u00f9ng thu\\u1ed1c n\\u1ebfu th\\u1ea5y SGPT (ALT) &gt; 100 \\u0111\\u01a1n v\\u1ecb qu\\u1ed1c t\\u1ebf. Kh\\u00f4ng k\\u1ebft h\\u1ee3p fenofibrat v\\u1edbi c\\u00e1c thu\\u1ed1c c\\u00f3 t\\u00e1c d\\u1ee5ng \\u0111\\u1ed9c v\\u1edbi gan. Bi\\u1ebfn ch\\u1ee9ng m\\u1eadt d\\u1ec5 x\\u1ea3y ra \\u1edf ng\\u01b0\\u1eddi b\\u1ec7nh x\\u01a1 \\u1ee9 gan m\\u1eadt ho\\u1eb7c c\\u00f3 s\\u1ecfi m\\u1eadt. N\\u1ebfu sau v\\u00e0i th\\u00e1ng d\\u00f9ng thu\\u1ed1c (3 - 6 th\\u00e1ng) m\\u00e0 th\\u1ea5y r\\u1ed1i lo\\u1ea1n lipid m\\u00e1u kh\\u00f4ng \\u0111\\u01b0\\u1ee3c c\\u1ea3i thi\\u1ec7n th\\u00ec ph\\u1ea3i xem x\\u00e9t tr\\u1ecb li\\u1ec7u kh\\u00e1c (b\\u1ed5 sung ho\\u1eb7c thay thu\\u1ed1c kh\\u00e1c).<\\/div><div><br><\\/div><div>Kh\\u00f4ng d\\u00f9ng vi\\u00ean 300 mg cho tr\\u1ebb em; ch\\u1ec9 d\\u00f9ng vi\\u00ean c\\u00f3 h\\u00e0m l\\u01b0\\u1ee3ng n\\u00e0y cho ng\\u01b0\\u1eddi b\\u1ec7nh c\\u1ea7n d\\u00f9ng li\\u1ec1u 300 mg fenofibrat\\/ng\\u00e0y.<\\/div><div><br><\\/div><div><span style=\\\"font-weight: bold;\\\">D\\u1ea1ng thu\\u1ed1c v\\u00e0 h\\u00e0m l\\u01b0\\u1ee3ng<\\/span><\\/div><div><br><\\/div><div> Nang 67 mg, 100 mg, 200 mg (vi h\\u1ea1t), 160 mg (gi\\u1ea3i ph\\u00f3ng c\\u00f3 ki\\u1ec3m so\\u00e1t)<\\/div><div><br><\\/div><div><span style=\\\"font-weight: bold;\\\">Qu\\u00e1 li\\u1ec1u v\\u00e0 x\\u1eed tr\\u00ed<\\/span><\\/div><div><br><\\/div><div> Th\\u1ea9m t\\u00e1ch m\\u00e1u kh\\u00f4ng c\\u00f3 t\\u00e1c d\\u1ee5ng lo\\u1ea1i b\\u1ecf thu\\u1ed1c kh\\u1ecfi c\\u01a1 th\\u1ec3.<\\/div><div><br><\\/div><div><span style=\\\"font-weight: bold;\\\">C\\u1ea7n b\\u1ea3o qu\\u1ea3n thu\\u1ed1c n\\u00e0y nh\\u01b0 th\\u1ebf n\\u00e0o?<\\/span><\\/div><div><br><\\/div><div>B\\u1ea3o qu\\u1ea3n thu\\u1ed1c n\\u01a1i kh\\u00f4 r\\u00e1o, tr\\u00e1nh \\u1ea9m.<\\/div><div><br><\\/div><div><span style=\\\"font-weight: bold;\\\">Khuy\\u1ebfn c\\u00e1o<\\/span><\\/div><div><br><\\/div><div>\\u0110\\u1ecdc k\\u1ef9 h\\u01b0\\u1edbng d\\u1eabn s\\u1eed d\\u1ee5ng tr\\u01b0\\u1edbc khi d\\u00f9ng<\\/div><div>\\u0110\\u1ec3 xa t\\u1ea7m tay tr\\u1ebb em<\\/div><div>Th\\u00f4ng b\\u00e1o ngay cho b\\u00e1c s\\u1ef9 ho\\u1eb7c d\\u01b0\\u1ee3c s\\u1ef9 nh\\u1eefng t\\u00e1c d\\u1ee5ng kh\\u00f4ng mong mu\\u1ed1n g\\u1eb7p ph\\u1ea3i khi s\\u1eed d\\u1ee5ng thu\\u1ed1c<\\/div><div>Thu\\u1ed1c n\\u00e0y ch\\u1ec9 d\\u00f9ng theo \\u0111\\u01a1n c\\u1ee7a b\\u00e1c s\\u1ef9<\\/div><div><br><\\/div><div><span style=\\\"font-weight: bold;\\\">L\\u01b0u \\u00fd<\\/span><\\/div><div><span style=\\\"font-weight: bold;\\\"><br><\\/span><\\/div><div>Tr\\u00ean \\u0111\\u00e2y l\\u00e0 nh\\u1eefng th\\u00f4ng tin v\\u1ec1 ho\\u1ea1t ch\\u1ea5t, chi ti\\u1ebft v\\u1ec1 th\\u00e0nh ph\\u1ea7n, h\\u00e0m l\\u01b0\\u1ee3ng, quy c\\u00e1ch \\u0111\\u00f3ng g\\u00f3i, m\\u00f4 t\\u1ea3 s\\u1ea3n ph\\u1ea7m v\\u00e0&nbsp;<\\/div><div><br><\\/div><div>H\\u1ea1n s\\u1eed d\\u1ee5ng c\\u1ee7a t\\u1eebng lo\\u1ea1i bi\\u1ec7t d\\u01b0\\u1ee3c c\\u00f3 th\\u1ec3 kh\\u00e1c nhau t\\u00f9y theo nh\\u00e0 s\\u1ea3n xu\\u1ea5t.<\\/div><div><br><\\/div><div>Th\\u00f4ng tin ch\\u1ec9 mang t\\u00ednh ch\\u1ea5t tham kh\\u1ea3o, tuy\\u1ec7t \\u0111\\u1ed1i kh\\u00f4ng s\\u1eed d\\u1ee5ng thu\\u1ed1c khi ch\\u01b0a tham kh\\u1ea3o \\u00fd ki\\u1ebfn c\\u1ee7a b\\u00e1c s\\u1ef9, d\\u01b0\\u1ee3c s\\u1ef9<\\/div><\\/div>\",\"\",\"U\\u1ed1ng thu\\u1ed1c c\\u00f9ng v\\u1edbi b\\u1eefa \\u0103n. Nang 67 mg ph\\u00f9 h\\u1ee3p cho tr\\u1ebb em.<div><br><\\/div><div>Ng\\u01b0\\u1eddi l\\u1edbn, u\\u1ed1ng li\\u1ec1u ban \\u0111\\u1ea7u 200 mg\\/ng\\u00e0y, u\\u1ed1ng l\\u00e0m m\\u1ed9t l\\u1ea7n ho\\u1eb7c chia l\\u00e0m 2 l\\u1ea7n. C\\u1ea7n duy tr\\u00ec li\\u1ec1u ban \\u0111\\u1ea7u cho \\u0111\\u1ebfn khi cholesterol huy\\u1ebft tr\\u1edf l\\u1ea1i b\\u00ecnh th\\u01b0\\u1eddng. Ph\\u1ea3i ki\\u1ec3m tra cholesterol huy\\u1ebft 3 th\\u00e1ng m\\u1ed9t l\\u1ea7n. N\\u1ebfu cho- lesterol huy\\u1ebft c\\u00f2n cao, c\\u00f3 th\\u1ec3 t\\u0103ng li\\u1ec1u l\\u00ean 300 mg\\/ng\\u00e0y.<\\/div><div><br><\\/div><div>Tr\\u1ebb em tr\\u00ean 10 tu\\u1ed5i: Li\\u1ec1u t\\u1ed1i \\u0111a 5 mg\\/kg\\/ng\\u00e0y.<\\/div>\",\"\",\"\",\"\",\"\"]', 'fenofibrate, fenofibrat, ', '', '', '0');
