INSERT INTO `tbl_mnuitem_text` VALUES ('6', '6', 'Hỏi bác sỹ', '<div style=\"text-align: justify;\">Nơi người bệnh có thể nhận được Hướng dẫn khám chữa bệnh và phòng bệnh của bác sỹ giàu kinh nghiệm tại các bệnh viện đầu ngành trên toàn quốc, đúng như mục tiêu của Antoandunthuoc.com.vn là trở thành cầu nối giữa bác sỹ và người bệnh.</div>', '0', null);
INSERT INTO `tbl_mnuitem_text` VALUES ('12', '12', 'Chuẩn bị trước khám', '<div style=\"text-align: justify;\">Là một nội dung quan trọng có ảnh hưởng đến chất lượng của việc thăm khám. Chuẩn bị trước khám sẽ cung cấp danh mục các việc cần làm, cần chuẩn bị, các câu hỏi cần lưu ý trước khi gặp bác sỹ.</div>', '0', null);
INSERT INTO `tbl_mnuitem_text` VALUES ('8', '8', 'Địa chỉ khám bệnh', '<div style=\"text-align: justify;\">Cập nhật danh sách và lịch khám bệnh theo từng chuyên khoa của các bệnh viện, phòng khám trên khắp 63 tỉnh/thành phố. Antoandungthuoc.com.vn là kênh thông tin trung thực giúp bạn tìm đúng thầy thuốc.</div>', '0', null);
INSERT INTO `tbl_mnuitem_text` VALUES ('9', '9', 'An toàn dùng thuốc', '<div style=\"text-align: justify;\"><span style=\"font-weight: bold; font-style: italic;\">Tổng thể</span> về bệnh học và <span style=\"font-weight: bold; font-style: italic;\">chi tiết</span> cách sử dụng từng loại thuốc trên <span style=\"font-weight: bold; font-style: italic;\">mỗi đối tượng người bệnh</span>, đặc biệt với những căn bệnh đang là gánh nặng to lớn của cộng đồng.</div>', '0', null);
INSERT INTO `tbl_mnuitem_text` VALUES ('10', '10', 'Từ điển thuốc', '<div style=\"text-align: justify;\">Thông tin về hoạt chất và biệt dược được biên soạn một cách khoa học, hữu ích. Đây là từ điển thuốc đầu tiên tại Việt Nam được thực hiện theo quy định của Bộ y tế, trong đó có Phần Hướng dẫn sử dụng dành riêng cho bệnh nhân và cán bộ y tế. Nguồn tài liệu:</div><div style=\"text-align: justify;\">Dược thư quốc gia &nbsp;– tài liệu chính thức hướng dẫn sử dụng thuốc an toàn, hiệu quả, hợp lý do Bộ Y tế ban hành.</div><div style=\"text-align: justify;\">Trang thông tin thuốc nước ngoài được Bộ Y tế khuyến cáo: Cơ quan quản lý Thuốc và Sản phầm y tế Anh quốc (EMC), Cục Quản lý Thực phẩm và Dược phẩm Hoa Kỳ (FDA).</div>', '0', null);
INSERT INTO `tbl_mnuitem_text` VALUES ('11', '11', 'Hỏi đáp', '<div style=\"text-align: justify;\">Khi những bài viết và phần tra cứu từ điển thuốc không làm bạn hài lòng, bạn có thể trực tiếp gửi câu hỏi của mình về cho đội ngũ An toàn dùng thuốc. Trong vòng 24h, chúng tôi sẽ nỗ lực hết sức mình để tìm câu trả lời thuyết phục nhất cho bạn</div>', '0', null);
